import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Container, Typography, Grid, Alert } from '@mui/material';

const UpdateTicketPrice = () => {
  const [ticketId, setTicketId] = useState('');
  const [price, setPrice] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);

    try {
      const response = await axios.patch(`http://localhost:5000/tickets/${ticketId}/price`, { sellingPrice: price });
      setSuccess('Ticket price updated successfully!');
    } catch (error) {
      setError(error.response ? error.response.data.error : 'Error updating ticket price!');
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Update Ticket Price
      </Typography>
      {error && <Alert severity="error">{error}</Alert>}
      {success && <Alert severity="success">{success}</Alert>}
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              type="text"
              name="ticketId"
              label="Ticket ID"
              fullWidth
              value={ticketId}
              onChange={(e) => setTicketId(e.target.value)}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              type="text"
              name="price"
              label="New Price"
              fullWidth
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
          </Grid>
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Update Price
            </Button>
          </Grid>
        </Grid>
      </form>
    </Container>
  );
};

export default UpdateTicketPrice;
