import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Container, Typography, Grid } from '@mui/material';

const ValidateTicket = () => {
  const [ticketId, setTicketId] = useState('');
  const [validatedOn, setValidatedOn] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.patch(`http://localhost:5000/tickets/${ticketId}/validate`, { validatedOn });
      console.log(response.data);
      alert('Ticket validated successfully!');
    } catch (error) {
      console.error(error);
      alert('Error validating ticket!');
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Validate Ticket
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              type="text"
              name="ticketId"
              label="Ticket ID"
              fullWidth
              value={ticketId}
              onChange={(e) => setTicketId(e.target.value)}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              type="text"
              name="validatedOn"
              label="Validated On (YYYY-MM-DD)"
              fullWidth
              value={validatedOn}
              onChange={(e) => setValidatedOn(e.target.value)}
            />
          </Grid>
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Validate Ticket
            </Button>
          </Grid>
        </Grid>
      </form>
    </Container>
  );
};

export default ValidateTicket;
